import React, { Component } from 'react';
import { DragonCard } from './DragonCard';
import { dragons }  from './data' //added

export class Home extends Component {

    constructor(){
        super()
        this.state = {
          dragons: dragons
        }
      }
    render() {
        // console.log("Home Component")

        let dragonsAtHome = this.state.dragons.filter( dragon => !dragon.atWar )

        return (
            <div style={{float:'left',width:'100%', backgroundColor:'#00ffd8', textAlign:'center'}}>
                <h1>Home</h1>
                {this.props.dragons.map( dragon => (
                    <DragonCard {...dragon} selectDragon={this.props.selectDragon} />
                ))}
            </div>
        );
    }
}
