import React, { Component } from 'react'
import { dragons }  from './data'

export class DragonCard extends Component {
    constructor(){
        super()
        if(this.props.dragon){

        }
        else{this.state={
            dragon: dragons.find( dragon => (dragon.id==this.props.match.params.id) )
        }}
    }


    allDragons = () => {
        this.props.history.push(`/dragons`)
    }

    render(){
        console.log(this.props, this.state)
        return (

            <div>
             <h1> Dragon's card</h1>
                <h3>{this.state.dragon.name}</h3>
                <p>{this.state.dragon.description}</p>
                <img style={{ width: '100px'}} src={this.state.dragon.image} alt=""></img>
                <h3 onClick={this.allDragons}>Show all Dragons</h3>
            </div>
        )
    }

}
