import React, { Component } from 'react';
import { dragons }  from './data' // in home & war
import { War } from './War';
import { Home } from './Home';
import { DragonCard } from './DragonCard'
import { BrowserRouter, Route} from 'react-router-dom'


let updateElementInArray = (array, id, values) => {
  return array.map( element => {
    if(element.id === id){
      return { ...element, ...values }
    } else {
      return element
    }
  })
}

export class Dragons extends Component {

  constructor(props){
    super(props)
    this.state = {
      dragons: dragons,
      selected: null
    }
  }

  sendDragonToWar = (id) => {
    this.setState({
      dragons: updateElementInArray(this.state.dragons, id, { atWar: true })
    })
  }

  sendDragonHome = (id) => {
    this.setState({
      dragons: updateElementInArray(this.state.dragons, id, { atWar: false })
    })
  }

  changeSelected = (selected) => {
    this.setState({
     selected: selected
    })
  }

  render() {
    return (
        <div>
            <h2> All Dragons</h2>

            {this.state.dragons.map( dragon => (   
                    <div>
                       <h3>{dragon.name}</h3>
                       <p>{dragon.description}</p>
                       <img style={{ width: '100px'}} src={dragon.image} alt=""></img>
                    </div>
            ))}

        </div>
    )
  }
}
