import React, { Component } from 'react';
import { DragonCard } from './DragonCard'
import { dragons }  from './data' //added
import { Link} from 'react-router-dom'

export class War extends Component {
    render() {
        return (
            <div style={{float:'left', width:'100%', backgroundColor:'#f98181', textAlign:'center'}}>
                <h1>War</h1>
                {this.props.dragons.map( dragon => (
                <Link to="/home">
                 <DragonCard dragon={dragon} selectDragon={this.props.sendDragonToWar}/>

                </Link>
                ))}
                {/* <Link to="/home"> see home dragon </Link> */}

            </div>
        );
    }
}

