import React, { Component } from 'react';
import { DragonCard } from './DragonCard';
import { dragons }  from './data' //added
import { Link} from 'react-router-dom'

export class Home extends Component {
        state = {
          dragons: dragons.filter( dragon => !dragon.atWar )
        }

        sendDragonToWar = (id) => {
            this.setState({
              dragons: updateElementInArray(this.state.dragons, id, { atWar: true })
            })
        }

    render() {
        // console.log("Home Component")

        return (
            <div style={{float:'left',width:'100%', backgroundColor:'#00ffd8', textAlign:'center'}}>
                <h1>Home</h1>
                {this.state.dragons.map( dragon => (
                    <div onClick={() => this.sendDragonToWar(dragon.id) }>
                       <h3>{dragon.name}</h3>
                       <p>{dragon.description}</p>
                       <img style={{ width: '100px'}} src={dragon.image} alt=""></img>
                    </div>

                ))}
            </div>
        );
    }
}
