import React, { Component } from 'react';
import { DragonCard } from './DragonCard';
import { dragons }  from './data' //added

export class Home extends Component {
        state = {
          dragons: dragons.filter( dragon => !dragon.atWar )
        }

    render() {
        // console.log("Home Component")

        return (
            <div style={{float:'left',width:'100%', backgroundColor:'#00ffd8', textAlign:'center'}}>
                <h1>Home</h1>
                {this.state.dragons.map( dragon => (
                    <div onClick={(e) => this.props.selectDragon(this.props.id)}>
                    <h1> Dragon's card</h1>
                       <h3>{this.props.name}</h3>
                       <p>{this.props.description}</p>
                       <img style={{ width: '100px'}} src={this.props.image} alt=""></img>
                   </div>
                ))}
            </div>
        );
    }
}
