import React, { Component } from 'react'
import { dragons }  from './data'

export class DragonCard extends Component {
    state={
        dragon: dragons.filter( dragon => (dragon.id==this.props.match.params.id) )
    }


    render(){
        console.log(this.props)
        return (


            <div onClick={(e) => this.props.selectDragon(this.props.id)}>
             <h1> Dragon's card</h1>
                <h3>{this.state.dragon.name}</h3>
                <p>{this.props.description}</p>
                <img style={{ width: '100px'}} src={this.props.image} alt=""></img>
            </div>
        )
    }

}
